CREATE TABLE "ANONYMOUS"."Customers" (
    "CUS_Id" NUMBER(38,0) GENERATED BY DEFAULT AS IDENTITY NOT NULL,
	"CUS_FirstName" VARCHAR2(50) NOT NULL,
	"CUS_LastName" VARCHAR2(50) NOT NULL,
	"CUS_Email" VARCHAR2(50) NOT NULL,
	"CUS_CreationDate" TIMESTAMP NOT NULL
);

ALTER TABLE "ANONYMOUS"."Customers" ADD CONSTRAINT "PK_Customers" PRIMARY KEY ("CUS_Id");

CREATE TABLE "ANONYMOUS"."Countries"(
	"COU_Id" NUMBER(38,0) GENERATED BY DEFAULT AS IDENTITY NOT NULL,
	"COU_Name" VARCHAR2(50) NOT NULL
);

ALTER TABLE "ANONYMOUS"."Countries" ADD CONSTRAINT "PK_Countries" PRIMARY KEY ("COU_Id");
ALTER TABLE "ANONYMOUS"."Countries" ADD CONSTRAINT "UQ_Countries_Name" UNIQUE ("COU_Name");

CREATE TABLE "ANONYMOUS"."Addresses" (
    "ADR_Id" NUMBER(38,0) GENERATED BY DEFAULT AS IDENTITY NOT NULL,
	"ADR_Street" VARCHAR2(50) NOT NULL,
	"ADR_PostalCode" VARCHAR2(50) NULL,
	"ADR_City" VARCHAR2(50) NOT NULL,
	"ADR_IsMain" NUMBER(3) NOT NULL,
	"COU_Id" NUMBER(38,0) NOT NULL,
	"CUS_Id" NUMBER(38,0) NOT NULL
);

ALTER TABLE "ANONYMOUS"."Addresses" ADD CONSTRAINT "PK_Addresses" PRIMARY KEY ("ADR_Id");
ALTER TABLE "ANONYMOUS"."Addresses" ADD CONSTRAINT "FK_Addresses_Customers" FOREIGN KEY ("CUS_Id") REFERENCES "ANONYMOUS"."Customers" ("CUS_Id");
ALTER TABLE "ANONYMOUS"."Addresses" ADD CONSTRAINT "FK_Addresses_Countries" FOREIGN KEY ("COU_Id") REFERENCES "ANONYMOUS"."Countries" ("COU_Id");
